  onDrop(event: CdkDragDrop<StatCard[]>) {
    if (this.isEditMode) {
      const draggedElement = event.item.element.nativeElement;
      const previousIndex = event.previousIndex;
      const cards = Array.from(this.dropList?.element.nativeElement.children || []);

      // Try to get the pointer position from the event
      let pointerX: number | null = null;
      let pointerY: number | null = null;
      if (event.dropPoint) {
        // Angular CDK 16+ provides dropPoint
        pointerX = event.dropPoint.x;
        pointerY = event.dropPoint.y;
      } else if ((event as any).event && (event as any).event.clientX !== undefined) {
        // Fallback for older versions or custom events
        pointerX = (event as any).event.clientX;
        pointerY = (event as any).event.clientY;
      }

      // If pointer position is not available, use the center of the dragged element
      let dropCenter = { x: 0, y: 0 };
      if (pointerX !== null && pointerY !== null) {
        dropCenter = { x: pointerX, y: pointerY };
      } else {
        const draggedRect = draggedElement.getBoundingClientRect();
        dropCenter = {
          x: draggedRect.left + draggedRect.width / 2,
          y: draggedRect.top + draggedRect.height / 2
        };
      }

      // Find the card whose center is closest to the drop center (excluding the dragged card itself)
      let minDistance = Number.MAX_VALUE;
      let nearestIndex = previousIndex;
      cards.forEach((card: any, idx: number) => {
        if (idx === previousIndex) return; // skip the dragged card itself
        const rect = card.getBoundingClientRect();
        const center = {
          x: rect.left + rect.width / 2,
          y: rect.top + rect.height / 2
        };
        const distance = Math.sqrt(
          Math.pow(center.x - dropCenter.x, 2) +
          Math.pow(center.y - dropCenter.y, 2)
        );
        if (distance < minDistance) {
          minDistance = distance;
          nearestIndex = idx;
        }
      });

      // Add swap animation classes
      this.renderer.addClass(draggedElement, 'swapping');
      if (cards[nearestIndex]) {
        this.renderer.addClass(cards[nearestIndex], 'swapping');
      }

      const draggedRect = draggedElement.getBoundingClientRect();
      const targetRect = cards[nearestIndex]?.getBoundingClientRect();
      if (targetRect) {
        this.renderer.setStyle(draggedElement, 'transform', `translate(${targetRect.left - draggedRect.left}px, ${targetRect.top - draggedRect.top}px)`);
        this.renderer.setStyle(cards[nearestIndex], 'transform', `translate(${draggedRect.left - targetRect.left}px, ${draggedRect.top - targetRect.top}px)`);
      }

      setTimeout(() => {
        // Swap only the dragged and nearest cards
        if (previousIndex !== nearestIndex) {
          const temp = this.statCards[previousIndex];
          this.statCards[previousIndex] = this.statCards[nearestIndex];
          this.statCards[nearestIndex] = temp;
        }
        this.renderer.removeStyle(draggedElement, 'transform');
        if (cards[nearestIndex]) {
          this.renderer.removeStyle(cards[nearestIndex], 'transform');
          this.renderer.removeClass(cards[nearestIndex], 'swapping');
        }
        this.renderer.removeClass(draggedElement, 'swapping');
        this.dropList?.element.nativeElement.offsetHeight;
      }, 300);
    }
  }

  onDragStarted(event: any) {
    if (this.isEditMode) {
      document.body.classList.add('dragging');

      // Add a class to the dragged element
      const draggedElement = event.source.element.nativeElement;
      this.renderer.addClass(draggedElement, 'dragging');

      // Add a class to the drop list
      if (this.dropList) {
        const dropListElement = this.dropList.element.nativeElement;
        this.renderer.addClass(dropListElement, 'dragging-active');
        this.renderer.addClass(dropListElement, 'edit-mode');
      }
    }
  }

  onDragEnded(event: any) {
    document.body.classList.remove('dragging');

    // Remove classes from the dragged element
    const draggedElement = event.source.element.nativeElement;
    this.renderer.removeClass(draggedElement, 'dragging');
    this.renderer.removeClass(draggedElement, 'swapping');
    this.renderer.removeStyle(draggedElement, 'transform');

    // Remove class from the drop list
    if (this.dropList) {
      const dropListElement = this.dropList.element.nativeElement;
      this.renderer.removeClass(dropListElement, 'dragging-active');
    }
  }

