      styles: ["[_nghost-%COMP%] {\n  display: block;\n  width: 100%;\n  height: 100vh;\n}\n\n[_ngcontent-%COMP%]:root {\n  --primary-color: #0c7ff2;\n  --background-color: #f0f2f5;\n  --text-primary: #111418;\n  --text-secondary: #60758a;\n  --border-color: #dbe0e6;\n}\n\n.admin-dashboard-container[_ngcontent-%COMP%] {\n  width: 100vw;\n  height: 100vh;\n  background-color: var(--background-color);\n  overflow: hidden;\n}\n\n.dashboard-main[_ngcontent-%COMP%] {\n  width: 100%;\n  height: 100%;\n  padding: 2rem;\n  box-sizing: border-box;\n  overflow-y: auto;\n}\n\n.dashboard-content[_ngcontent-%COMP%] {\n  width: 100%;\n  max-width: none;\n}\n\n.page-header[_ngcontent-%COMP%] {\n  margin-bottom: 2rem;\n}\n\n.page-title[_ngcontent-%COMP%] {\n  font-size: 2.5rem;\n  font-weight: 700;\n  color: var(--text-primary);\n  margin: 0;\n}\n\n.stats-section[_ngcontent-%COMP%] {\n  margin-bottom: 3rem;\n}\n\n.stats-grid[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));\n  gap: 1.5rem;\n  width: 100%;\n}\n\n.stat-card[_ngcontent-%COMP%] {\n  background: white;\n  padding: 2rem;\n  border-radius: 12px;\n  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);\n  border: 1px solid var(--border-color);\n}\n\n.stat-label[_ngcontent-%COMP%] {\n  color: var(--text-secondary);\n  font-size: 0.875rem;\n  font-weight: 500;\n  margin: 0 0 0.5rem 0;\n}\n\n.stat-value[_ngcontent-%COMP%] {\n  color: var(--text-primary);\n  font-size: 3rem;\n  font-weight: 700;\n  margin: 0;\n}\n\n.analytics-section[_ngcontent-%COMP%] {\n  margin-bottom: 3rem;\n}\n\n.section-title[_ngcontent-%COMP%] {\n  font-size: 1.75rem;\n  font-weight: 600;\n  color: var(--text-primary);\n  margin: 0 0 1.5rem 0;\n}\n\n.analytics-grid[_ngcontent-%COMP%] {\n  display: grid;\n  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));\n  gap: 1.5rem;\n  width: 100%;\n}\n\n.analytics-card[_ngcontent-%COMP%] {\n  background: white;\n  padding: 1.5rem;\n  border-radius: 12px;\n  