    {
      icon: 'person_add',
      text: 'New employee John Doe joined the IT department',
      time: '2 hours ago'
    },
    {
      icon: 'edit',
      text: 'Sarah Johnson updated her profile information',
      time: '4 hours ago'
    },
    {
      icon: 'event',
      text: 'Team meeting scheduled for tomorrow at 10 AM',
      time: '5 hours ago'
    },
    {
      icon: 'assessment',
      text: 'Monthly performance reports are ready',
      time: '1 day ago'
    }
  ];

  // Pagination properties
  currentPage = 1;
  pageSize = 5;
  totalItems = 0;
  totalPages = 0;
  searchTerm = '';
  paginatedEmployees: Employee[] = [];
  Math = Math; // Make Math available in template

  showEditModal = false;
  editEmployeeForm: FormGroup;
  selectedEmployee: Employee | null = null;

  widgets: WidgetConfig[] = [];
  showWidgetConfig = false;

  constructor(
    private router: Router,
    private fb: FormBuilder,
    private widgetConfigService: WidgetConfigService
  ) {
    this.editEmployeeForm = this.fb.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      department: ['', Validators.required],
      position: ['', Validators.required],
      hireDate: ['', Validators.required],
      status: ['Active', Validators.required]
    });
  }

  @HostListener('window:scroll', ['$event'])
  onScroll() {
    this.checkScroll();
  }

  ngOnInit(): void {
    // Initialize component
    this.updatePagination();
    // Initial check for elements in view
    setTimeout(() => {
      this.checkScroll();
    }, 100);
    this.loadWidgets();
  }

  // Pagination methods
  updatePagination() {
    // Filter employees based on search term
    let filteredEmployees = this.recentEmployees;
    if (this.searchTerm) {
      const searchLower = this.searchTerm.toLowerCase();
      filteredEmployees = this.recentEmployees.filter(emp => 
        emp.firstName.toLowerCase().includes(searchLower) ||
        emp.lastName.toLowerCase().includes(searchLower) ||
        emp.department.toLowerCase().includes(searchLower) ||
        emp.position.toLowerCase().includes(searchLower) ||
        emp.status.toLowerCase().includes(searchLower)
      );
    }

    this.totalItems = filteredEmployees.length;
    this.totalPages = Math.ceil(this.totalItems / this.pageSize);

    // Ensure current page is valid
    if (this.currentPage > this.totalPages) {
      this.currentPage = this.totalPages || 1;
    }

    // Get paginated data
    const startIndex = (this.currentPage - 1) * this.pageSize;
    this.paginatedEmployees = filteredEmployees.slice(startIndex, startIndex + this.pageSize);
  }

  onPageChange(page: number) {
    this.currentPage = page;
    this.updatePagination();
    // Scroll to bottom of the page
    setTimeout(() => {
      window.scrollTo({
        top: document.documentElement.scrollHeight,
        behavior: 'smooth'
      });
    }, 100); // Small delay to ensure content is updated
  }

  onPageSizeChange(event: Event) {
    const select = event.target as HTMLSelectElement;
    this.pageSize = parseInt(select.value, 10);
    this.currentPage = 1; // Reset to first page when changing page size
    this.updatePagination();
  }

  onSearch(event: Event) {
    const input = event.target as HTMLInputElement;
    this.searchTerm = input.value;
    this.currentPage = 1; // Reset to first page when searching
    this.updatePagination();
  }

  getPageNumbers(): number[] {
    const pages: number[] = [];
    const maxVisiblePages = 5;

    if (this.totalPages <= maxVisiblePages) {
      // Show all pages if total pages is less than max visible
      for (let i = 1; i <= this.totalPages; i++) {
        pages.push(i);
      }
    } else {
      // Always show first page
      pages.push(1);

      // Calculate start and end of visible pages
      let start = Math.max(2, this.currentPage - 1);
      let end = Math.min(this.totalPages - 1, this.currentPage + 1);

      // Adjust if at the start
      if (this.currentPage <= 2) {
        end = 4;
      }
      // Adjust if at the end
      if (this.currentPage >= this.totalPages - 1) {
        start = this.totalPages - 3;
      }

      // Add ellipsis if needed
      if (start > 2) {
        pages.push(-1); // -1 represents ellipsis
      }

      // Add middle pages
      for (let i = start; i <= end; i++) {
        pages.push(i);
      }

      // Add ellipsis if needed
      if (end < this.totalPages - 1) {
        pages.push(-2); // -2 represents ellipsis
      }

      // Always show last page
      pages.push(this.totalPages);
    }

    return pages;
  }

  onAddEmployee(): void {
    this.router.navigate(['/personnel-information-management/add-employee']);
  }

  onEditEmployee(employee: Employee) {
    this.selectedEmployee = employee;
    this.editEmployeeForm.patchValue({
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
      department: employee.department,
      position: employee.position,
      hireDate: employee.hireDate,
      status: employee.status
    });
    this.showEditModal = true;
  }

  closeEditModal(event: Event) {
    event.preventDefault();
    this.showEditModal = false;
    this.selectedEmployee = null;
    this.editEmployeeForm.reset();
  }

  onSubmitEdit() {
    if (this.editEmployeeForm.valid && this.selectedEmployee) {
      // Here you would typically make an API call to update the employee
      const updatedEmployee = {
        ...this.selectedEmployee,
        ...this.editEmployeeForm.value
      };

      // Update the employee in the local array
      const index = this.recentEmployees.findIndex(emp => emp.id === this.selectedEmployee?.id);
      if (index !== -1) {
        this.recentEmployees[index] = updatedEmployee;
      }

      // Close the modal
      this.closeEditModal(new Event('click'));
    }
  }

  onDeleteEmployee(employee: Employee): void {
    if (confirm(`Are you sure you want to delete ${employee.firstName} ${employee.lastName}?`)) {
      // Call service to delete employee
      console.log('Deleting employee:', employee);
    }
  }

  onProcessRequests(): void {
    this.router.navigate(['/personnel-information-management/requests']);
  }

  getDepartmentIcon(department: string): string {
    const icons: { [key: string]: string } = {
      'IT': 'computer',
      'HR': 'group',
      'Finance': 'account_balance',
      'Marketing': 'campaign',
      'Operations': 'settings',
      'Engineering': 'engineering',
      'Sales': 'trending_up',
      'Customer Support': 'support_agent',
      'Research': 'science',
      'Legal': 'gavel',
      'Product': 'inventory_2',
      'Design': 'palette'
    };
    return icons[department] || 'business';
  }

  private checkScroll() {
    const elements = document.querySelectorAll('.scroll-animate');
    elements.forEach(element => {
      const rect = element.getBoundingClientRect();
      const isVisible = (rect.top <= window.innerHeight * 0.95);

      if (isVisible) {
        element.classList.add('visible');
      }
    });
  }

  loadWidgets() {
    this.widgetConfigService.getWidgets().subscribe(widgets => {
      this.widgets = widgets.filter(w => w.enabled)
        .sort((a, b) => a.position - b.position);
    });
  }

  toggleWidgetConfig() {
    this.showWidgetConfig = !this.showWidgetConfig;
  }
}
