export class WidgetConfigComponent implements OnInit {
  widgets: WidgetConfig[] = [];
  showConfigModal = false;
  editingWidget: WidgetConfig | null = null;
  isDragging = '';
  widgetForm: FormGroup;

  constructor(private fb: FormBuilder) {
    this.widgetForm = this.fb.group({
      title: ['', Validators.required],
      type: ['', Validators.required],
      size: ['medium', Validators.required],
      position: [0, Validators.required],
      enabled: [true]
    });
  }

  ngOnInit() {
    // Load saved widget configurations
    this.loadWidgets();
  }

  loadWidgets() {
    // TODO: Load from service/API
    this.widgets = [
      {
        id: '1',
        type: 'employee-stats',
        title: 'Employee Overview',
        position: 1,
        size: 'medium',
        enabled: true,
        settings: {}
      },
      {
        id: '2',
        type: 'department-distribution',
        title: 'Department Stats',
        position: 2,
        size: 'large',
        enabled: true,
        settings: {}
      }
    ];
  }

  addNewWidget() {
    this.editingWidget = null;
    this.widgetForm.reset({
      size: 'medium',
      position: this.widgets.length + 1,
      enabled: true
    });
    this.showConfigModal = true;
  }

  editWidget(widget: WidgetConfig) {
    this.editingWidget = widget;
    this.widgetForm.patchValue(widget);
    this.showConfigModal = true;
  }

  saveWidget() {
    if (this.widgetForm.valid) {
      const formValue = this.widgetForm.value;
      
      if (this.editingWidget) {
        // Update existing widget
        const index = this.widgets.findIndex(w => w.id === this.editingWidget?.id);
        if (index !== -1) {
          this.widgets[index] = {
            ...this.editingWidget,
            ...formValue
          };
        }
      } else {
        // Add new widget
        const newWidget: WidgetConfig = {
          id: Date.now().toString(),
          ...formValue,
          settings: {}
        };
        this.widgets.push(newWidget);
      }

      this.closeModal(new Event('click'));
      this.saveWidgets();
    }
  }

  deleteWidget(widget: WidgetConfig) {
    if (confirm(`Are you sure you want to delete the "${widget.title}" widget?`)) {
      this.widgets = this.widgets.filter(w => w.id !== widget.id);
      this.saveWidgets();
    }
  }

  toggleWidget(widget: WidgetConfig) {
    widget.enabled = !widget.enabled;
    this.saveWidgets();
  }

  startDrag(widget: WidgetConfig) {
    this.isDragging = widget.id;
    // TODO: Implement drag and drop functionality
  }

  closeModal(event: Event) {
    event.preventDefault();
    this.showConfigModal = false;
    this.editingWidget = null;
    this.widgetForm.reset();
  }

  private saveWidgets() {
    // TODO: Save to service/API
