import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface DepartmentStat {
  name: string;
  count: number;
  percentage: number;
}

interface Employee {
  id: number;
  name: string;
  email: string;
  department: string;
  position: string;
  status: string;
  joinDate: string;
  profileImage?: string;
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
  title = 'Personnel Information Management';
  
  // Dashboard stats
  totalEmployees = 345;
  employeeChange = 5;
  pendingRequests = 12;
  requestChange = -2;
  recentMovements = 23;
  movementChange = 3;

  // Analytics metrics
  turnoverRate = 8.5;
  turnoverRateChange = 1.2;
  satisfactionLevel = 4.2;
  satisfactionChange = 0.3;
  performanceRating = 85;
  performanceChange = 2.5;

  departmentStats: DepartmentStat[] = [
    { name: 'IT', count: 45, percentage: 30 },
    { name: 'HR', count: 30, percentage: 20 },
    { name: 'Finance', count: 25, percentage: 17 },
    { name: 'Marketing', count: 20, percentage: 13 },
    { name: 'Operations', count: 30, percentage: 20 }
  ];

  recentEmployees: Employee[] = [
    {
      id: 1,
      name: 'Sarah Johnson',
      email: 'sarah.j@acme.com',
      department: 'Marketing',
      position: 'Marketing Manager',
      status: 'Active',
      joinDate: '15 Jan 2020',
      profileImage: 'https://randomuser.me/api/portraits/women/44.jpg'
    },
    {
      id: 2,
      name: 'Michael Chen',
      email: 'michael.c@acme.com',
      department: 'Engineering',
      position: 'Senior Developer',
      status: 'Active',
      joinDate: '22 Mar 2019',
      profileImage: 'https://randomuser.me/api/portraits/men/32.jpg'
    },
    {
      id: 3,
      name: 'Emily Rodriguez',
      email: 'emily.r@acme.com',
      department: 'Human Resources',
      position: 'HR Specialist',
      status: 'On Leave',
      joinDate: '05 Aug 2021',
      profileImage: 'https://randomuser.me/api/portraits/women/68.jpg'
    },
    {
      id: 4,
      name: 'David Wilson',
      email: 'david.w@acme.com',
      department: 'Finance',
      position: 'Financial Analyst',
      status: 'Active',
      joinDate: '12 Nov 2022',
      profileImage: 'https://randomuser.me/api/portraits/men/75.jpg'
    }
  ];

  constructor(private router: Router) {}

  ngOnInit(): void {
    // Initialize component
  }

  onAddEmployee(): void {
    this.router.navigate(['/personnel-information-management/add-employee']);
  }

  onEditEmployee(employee: Employee): void {
    this.router.navigate(['/personnel-information-management/edit-employee', employee.id]);
  }

  onDeleteEmployee(employee: Employee): void {
    if (confirm(`Are you sure you want to delete ${employee.name}?`)) {
      // Call service to delete employee
      console.log('Deleting employee:', employee);
    }
  }

  onProcessRequests(): void {
    this.router.navigate(['/personnel-information-management/requests']);
  }

  getDepartmentIcon(department: string): string {
    const icons: { [key: string]: string } = {
      'IT': 'computer',
      'HR': 'group',
      'Finance': 'account_balance',
      'Marketing': 'campaign',
      'Operations': 'settings',
      'Engineering': 'engineering',
      'Sales': 'trending_up',
      'Customer Support': 'support_agent',
      'Research': 'science',
      'Legal': 'gavel',
      'Product': 'inventory_2',
      'Design': 'palette'
    };
    return icons[department] || 'business';
  }
}
