import { Component, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink, Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [CommonModule, RouterLink],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  @Output() toggleSidebar = new EventEmitter<void>();
  
  title = 'HRIS Frontend';
  currentUser$ = this.authService.currentUser$;
  
  userMenuItems = [
    { name: 'Profile', icon: 'ðŸ‘¤', action: 'profile' },
    { name: 'Settings', icon: 'âš™ï¸', action: 'settings' },
    { name: 'Logout', icon: 'ðŸšª', action: 'logout' }
  ];

  notifications = [
    { message: 'New leave request pending', time: '2 min ago', type: 'info' },
    { message: 'Payroll processed successfully', time: '1 hour ago', type: 'success' },
    { message: 'System maintenance scheduled', time: '3 hours ago', type: 'warning' }
  ];

  showUserMenu = false;
  showNotifications = false;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {}

  onToggleSidebar() {
    this.toggleSidebar.emit();
  }

  toggleUserMenu() {
    this.showUserMenu = !this.showUserMenu;
    this.showNotifications = false;
  }

  toggleNotifications() {
    this.showNotifications = !this.showNotifications;
    this.showUserMenu = false;
  }

  onUserAction(action: string) {
    console.log('User action:', action);
    this.showUserMenu = false;
    
    if (action === 'logout') {
      this.authService.logout();
      this.router.navigate(['/login']);
    }
  }

  onNotificationClick(notification: any) {
    console.log('Notification clicked:', notification);
    this.showNotifications = false;
  }
}
