import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface DepartmentStat {
  name: string;
  percentage: number;
}

interface Employee {
  id: number;
  name: string;
  email: string;
  department: string;
  position: string;
  status: string;
  joinDate: string;
  profileImage?: string; // Optional profile image URL
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
  title = 'Personnel Information Management';
  totalEmployees = 150;
  employeeChange = 5;
  pendingRequests = 12;
  requestChange = -2;
  recentMovements = 8;
  movementChange = 3;

  // Analytics metrics
  turnoverRate = 8.5;
  turnoverRateChange = 1.2;
  satisfactionLevel = 4.2;
  satisfactionChange = 0.3;
  performanceRating = 85;
  performanceChange = 2.5;

  departmentStats: DepartmentStat[] = [
    { name: 'IT', percentage: 30 },
    { name: 'HR', percentage: 20 },
    { name: 'Finance', percentage: 17 },
    { name: 'Marketing', percentage: 13 },
    { name: 'Operations', percentage: 20 }
  ];

  recentEmployees: Employee[] = [
    {
      id: 1,
      name: 'John Doe',
      email: 'john.doe@company.com',
      department: 'IT',
      position: 'Senior Developer',
      status: 'Active',
      joinDate: '2023-01-15',
      profileImage: 'https://ui-avatars.com/api/?name=John+Doe&background=1993e5&color=fff'
    },
    {
      id: 2,
      name: 'Jane Smith',
      email: 'jane.smith@company.com',
      department: 'HR',
      position: 'HR Manager',
      status: 'Active',
      joinDate: '2023-02-20'
    },
    {
      id: 3,
      name: 'Mike Johnson',
      email: 'mike.johnson@company.com',
      department: 'Finance',
      position: 'Financial Analyst',
      status: 'On Leave',
      joinDate: '2023-03-10',
      profileImage: 'https://ui-avatars.com/api/?name=Mike+Johnson&background=1993e5&color=fff'
    }
  ];

  constructor(private router: Router) {}

  ngOnInit(): void {
    // Initialize component
  }

  onAddEmployee(): void {
    this.router.navigate(['/personnel-information-management/add-employee']);
  }

  onEditEmployee(employee: Employee): void {
    this.router.navigate(['/personnel-information-management/edit-employee', employee.id]);
  }

  onDeleteEmployee(employee: Employee): void {
    if (confirm(`Are you sure you want to delete ${employee.name}?`)) {
      // Call service to delete employee
      console.log('Deleting employee:', employee);
    }
  }

  onProcessRequests(): void {
    this.router.navigate(['/personnel-information-management/requests']);
  }
}
