import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-payroll-management',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.scss']
})
export class PayrollManagementComponent {
  title = 'Payroll Management';
  
  payrollFeatures = [
    { name: 'Salary Processing', description: 'Calculate and process employee salaries', icon: 'ðŸ’°' },
    { name: 'Tax Calculations', description: 'Automated tax deductions and calculations', icon: 'ðŸ§®' },
    { name: 'Benefits Deductions', description: 'Manage benefits and insurance deductions', icon: 'ðŸ¥' },
    { name: 'Payslip Generation', description: 'Generate and distribute payslips', icon: 'ðŸ“„' },
    { name: 'Overtime Pay', description: 'Calculate overtime and bonus payments', icon: 'â°' },
    { name: 'Payroll Reports', description: 'Comprehensive payroll reporting and analytics', icon: 'ðŸ“Š' }
  ];

  // Dashboard cards data
  payrollStatus = {
    status: 'In Progress',
    lastRun: '2024-06-10',
    nextRun: '2024-06-25'
  };
  pendingAdjustments = 3;
  salaryStats = {
    totalPayroll: 1200000,
    avgSalary: 35000,
    employees: 34
  };

  // Preferences & thresholds
  preferences = {
    showStats: true,
    alertThreshold: 5
  };
  preferencesModalOpen = false;

  // Alerts data
  alerts = [
    { id: 1, employee: 'Juan Dela Cruz', type: 'Incomplete Tax Document', status: 'Open' },
    { id: 2, employee: 'Maria Santos', type: 'Salary Discrepancy', status: 'Open' },
    { id: 3, employee: 'Pedro Reyes', type: 'Missing Membership Contribution', status: 'Resolved' }
  ];
  editingAlert: any = null;

  openPreferencesModal() {
    this.preferencesModalOpen = true;
  }
  closePreferencesModal() {
    this.preferencesModalOpen = false;
  }
  editAlert(alert: any) {
    this.editingAlert = { ...alert };
  }
  saveAlert() {
    if (this.editingAlert) {
      const idx = this.alerts.findIndex(a => a.id === this.editingAlert.id);
      if (idx > -1) this.alerts[idx] = { ...this.editingAlert };
      this.editingAlert = null;
    }
  }
  deleteAlert(id: number) {
    this.alerts = this.alerts.filter(a => a.id !== id);
  }
  cancelEditAlert() {
    this.editingAlert = null;
  }
}
