import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { trigger, transition, style, animate, query, stagger } from '@angular/animations';

interface DepartmentStat {
  name: string;
  count: number;
  percentage: number;
}

interface Employee {
  id: number;
  name: string;
  email: string;
  department: string;
  position: string;
  status: string;
  joinDate: string;
  profileImage?: string;
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss'],
  animations: [
    trigger('fadeIn', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(10px)' }),
        animate('300ms ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
      ])
    ]),
    trigger('staggerIn', [
      transition('* => *', [
        query(':enter', [
          style({ opacity: 0, transform: 'translateY(10px)' }),
          stagger('50ms', [
            animate('300ms ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
          ])
        ], { optional: true })
      ])
    ]),
    trigger('slideInOut', [
      transition(':enter', [
        style({ transform: 'translateX(-100%)' }),
        animate('300ms ease-out', style({ transform: 'translateX(0)' }))
      ]),
      transition(':leave', [
        animate('300ms ease-in', style({ transform: 'translateX(100%)' }))
      ])
    ]),
    trigger('fadeInOut', [
      transition(':enter', [
        style({ opacity: 0 }),
        animate('300ms ease-out', style({ opacity: 1 }))
      ]),
      transition(':leave', [
        animate('300ms ease-in', style({ opacity: 0 }))
      ])
    ])
  ]
})
export class AdminDashboardComponent implements OnInit {
  title = 'Personnel Information Management';
  
  // Dashboard stats
  totalEmployees = 345;
  employeeChange = 5;
  pendingRequests = 12;
  requestChange = -2;
  recentMovements = 23;
  movementChange = 3;

  // Analytics metrics
  turnoverRate = 8.5;
  turnoverRateChange = 1.2;
  satisfactionLevel = 4.2;
  satisfactionChange = 0.3;
  performanceRating = 85;
  performanceChange = 2.5;

  departmentStats: DepartmentStat[] = [
    { name: 'IT', count: 45, percentage: 30 },
    { name: 'HR', count: 30, percentage: 20 },
    { name: 'Finance', count: 25, percentage: 17 },
    { name: 'Marketing', count: 20, percentage: 13 },
    { name: 'Operations', count: 30, percentage: 20 }
  ];

  recentEmployees: Employee[] = [
    {
      id: 1,
      name: 'Sarah Johnson',
      email: 'sarah.j@acme.com',
      department: 'Marketing',
      position: 'Marketing Manager',
      status: 'Active',
      joinDate: '15 Jan 2020',
      profileImage: 'https://randomuser.me/api/portraits/women/44.jpg'
    },
    {
      id: 2,
      name: 'Michael Chen',
      email: 'michael.c@acme.com',
      department: 'Engineering',
      position: 'Senior Developer',
      status: 'Active',
      joinDate: '22 Mar 2019',
      profileImage: 'https://randomuser.me/api/portraits/men/32.jpg'
    },
    {
      id: 3,
      name: 'Emily Rodriguez',
      email: 'emily.r@acme.com',
      department: 'Human Resources',
      position: 'HR Specialist',
      status: 'On Leave',
      joinDate: '05 Aug 2021',
      profileImage: 'https://randomuser.me/api/portraits/women/68.jpg'
    },
    {
      id: 4,
      name: 'David Wilson',
      email: 'david.w@acme.com',
      department: 'Finance',
      position: 'Financial Analyst',
      status: 'Active',
      joinDate: '12 Nov 2022',
      profileImage: 'https://randomuser.me/api/portraits/men/75.jpg'
    },
    {
      id: 5,
      name: 'Michael Chen',
      email: 'michael.c@acme.com',
      department: 'Engineering',
      position: 'Senior Developer',
      status: 'Active',
      joinDate: '22 Mar 2019',
      profileImage: 'https://randomuser.me/api/portraits/men/32.jpg'
    },
    {
      id: 6,
      name: 'Emily Rodriguez',
      email: 'emily.r@acme.com',
      department: 'Human Resources',
      position: 'HR Specialist',
      status: 'On Leave',
      joinDate: '05 Aug 2021',
      profileImage: 'https://randomuser.me/api/portraits/women/68.jpg'
    },
    {
      id: 7,
      name: 'David Wilson',
      email: 'david.w@acme.com',
      department: 'Finance',
      position: 'Financial Analyst',
      status: 'Active',
      joinDate: '12 Nov 2022',
      profileImage: 'https://randomuser.me/api/portraits/men/75.jpg'
    }
  ];

  // Pagination properties
  currentPage = 1;
  pageSize = 5;
  totalItems = 0;
  totalPages = 0;
  searchTerm = '';
  paginatedEmployees: Employee[] = [];
  Math = Math; // Make Math available in template

  // Animation states
  animationState = 'in';

  constructor(private router: Router) {}

  ngOnInit(): void {
    // Initialize component
    this.updatePagination();
  }

  // Pagination methods
  updatePagination() {
    // Filter employees based on search term
    let filteredEmployees = this.recentEmployees;
    if (this.searchTerm) {
      const searchLower = this.searchTerm.toLowerCase();
      filteredEmployees = this.recentEmployees.filter(emp => 
        emp.name.toLowerCase().includes(searchLower) ||
        emp.department.toLowerCase().includes(searchLower) ||
        emp.position.toLowerCase().includes(searchLower) ||
        emp.status.toLowerCase().includes(searchLower)
      );
    }

    this.totalItems = filteredEmployees.length;
    this.totalPages = Math.ceil(this.totalItems / this.pageSize);

    // Ensure current page is valid
    if (this.currentPage > this.totalPages) {
      this.currentPage = this.totalPages || 1;
    }

    // Get paginated data
    const startIndex = (this.currentPage - 1) * this.pageSize;
    this.paginatedEmployees = filteredEmployees.slice(startIndex, startIndex + this.pageSize);
  }

  onPageChange(page: number) {
    this.animationState = 'out';
    setTimeout(() => {
      this.currentPage = page;
      this.updatePagination();
      this.animationState = 'in';
    }, 300);
  }

  onPageSizeChange(event: Event) {
    const select = event.target as HTMLSelectElement;
    this.animationState = 'out';
    setTimeout(() => {
      this.pageSize = parseInt(select.value, 10);
      this.currentPage = 1;
      this.updatePagination();
      this.animationState = 'in';
    }, 300);
  }

  onSearch(event: Event) {
    const input = event.target as HTMLInputElement;
    this.animationState = 'out';
    setTimeout(() => {
      this.searchTerm = input.value;
      this.currentPage = 1;
      this.updatePagination();
      this.animationState = 'in';
    }, 300);
  }

  getPageNumbers(): number[] {
    const pages: number[] = [];
    const maxVisiblePages = 5;
    
    if (this.totalPages <= maxVisiblePages) {
      // Show all pages if total pages is less than max visible
      for (let i = 1; i <= this.totalPages; i++) {
        pages.push(i);
      }
    } else {
      // Always show first page
      pages.push(1);
      
      // Calculate start and end of visible pages
      let start = Math.max(2, this.currentPage - 1);
      let end = Math.min(this.totalPages - 1, this.currentPage + 1);
      
      // Adjust if at the start
      if (this.currentPage <= 2) {
        end = 4;
      }
      // Adjust if at the end
      if (this.currentPage >= this.totalPages - 1) {
        start = this.totalPages - 3;
      }
      
      // Add ellipsis if needed
      if (start > 2) {
        pages.push(-1); // -1 represents ellipsis
      }
      
      // Add middle pages
      for (let i = start; i <= end; i++) {
        pages.push(i);
      }
      
      // Add ellipsis if needed
      if (end < this.totalPages - 1) {
        pages.push(-2); // -2 represents ellipsis
      }
      
      // Always show last page
      pages.push(this.totalPages);
    }
    
    return pages;
  }

  onAddEmployee(): void {
    this.router.navigate(['/personnel-information-management/add-employee']);
  }

  onEditEmployee(employee: Employee): void {
    this.router.navigate(['/personnel-information-management/edit-employee', employee.id]);
  }

  onDeleteEmployee(employee: Employee): void {
    if (confirm(`Are you sure you want to delete ${employee.name}?`)) {
      // Call service to delete employee
      console.log('Deleting employee:', employee);
    }
  }

  onProcessRequests(): void {
    this.router.navigate(['/personnel-information-management/requests']);
  }

  getDepartmentIcon(department: string): string {
    const icons: { [key: string]: string } = {
      'IT': 'computer',
      'HR': 'group',
      'Finance': 'account_balance',
      'Marketing': 'campaign',
      'Operations': 'settings',
      'Engineering': 'engineering',
      'Sales': 'trending_up',
      'Customer Support': 'support_agent',
      'Research': 'science',
      'Legal': 'gavel',
      'Product': 'inventory_2',
      'Design': 'palette'
    };
    return icons[department] || 'business';
  }
}
