      badge: null
    },
    {
      name: 'Learning & Development',
      icon: 'üéì',
      path: '/learning-development',
      badge: '4'
    },
    {
      name: 'Health & Wellness',
      icon: 'üè•',
      path: '/health-wellness',
      badge: null
    }
  ];

  toggleSidebar() {
    this.sidebarToggle.emit();
  }

  onMenuItemClick() {
    // Close sidebar on mobile when menu item is clicked
    if (window.innerWidth <= 768) {
      this.toggleSidebar();
    }
  }
}
