import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterOutlet, Router } from '@angular/router';
import { HeaderComponent } from './shared/header/header.component';
import { SidebarComponent } from './shared/sidebar/sidebar.component';
import { LoginComponent } from './auth/login/login.component';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [
    CommonModule, 
    RouterOutlet, 
    HeaderComponent, 
    SidebarComponent, 
    LoginComponent
  ],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'HRIS Frontend';
  isAuthenticated = false;
  isSidebarOpen = true;
  
  features = [
    { name: 'System Administration', icon: '‚öôÔ∏è', route: '/system-administration', description: 'Manage system settings and configurations' },
    { name: 'Personnel Information Management', icon: 'üë•', route: '/personnel-information-management', description: 'Manage employee information and records' },
    { name: 'Employee Self-Service', icon: 'üë§', route: '/employee-self-service', description: 'Employee portal for self-service functions' },
    { name: 'Timekeeping & Attendance', icon: '‚è∞', route: '/timekeeping-attendance', description: 'Track employee time and attendance' },
    { name: 'Payroll Management', icon: 'üí∞', route: '/payroll-management', description: 'Manage payroll processing and calculations' },
    { name: 'Leave Management', icon: 'üìÖ', route: '/leave-management', description: 'Handle leave requests and approvals' },
    { name: 'Report Generation', icon: 'üìä', route: '/report-generation', description: 'Generate and view reports' },
    { name: 'Recruitment', icon: 'üéØ', route: '/recruitment', description: 'Manage recruitment and hiring process' },
    { name: 'Online Job Application Portal', icon: 'üåê', route: '/online-job-application-portal', description: 'External job application portal' },
    { name: 'Performance Management', icon: 'üìà', route: '/performance-management', description: 'Track and manage employee performance' },
    { name: 'Learning & Development', icon: 'üéì', route: '/learning-development', description: 'Manage training and development programs' },
    { name: 'Health & Wellness', icon: 'üè•', route: '/health-wellness', description: 'Health and wellness programs' }
  ];

  constructor(private router: Router) {}

  ngOnInit() {
    // Check authentication status on app initialization
    this.checkAuthStatus();
  }

  checkAuthStatus() {
    this.isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';
    
    // If authenticated and on login page, redirect to dashboard
    if (this.isAuthenticated && this.router.url === '/login') {
      this.router.navigate(['/dashboard']);
    }
  }

  onLoginSuccess() {
    this.isAuthenticated = true;
  }

  onLogout() {
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('user');
    this.isAuthenticated = false;
    this.router.navigate(['/login']);
  }

  toggleSidebar() {
    this.isSidebarOpen = !this.isSidebarOpen;
  }

  isFeaturePage(): boolean {
    const currentUrl = this.router.url;
    return currentUrl !== '/' && currentUrl !== '/dashboard' && currentUrl !== '/login';
  }
}
