import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

interface DashboardStat {
  label: string;
  value: string;
  icon: string;
  change: string;
  trend: 'up' | 'down';
}

interface DepartmentStat {
  name: string;
  employeeCount: number;
  activeProjects: number;
  leaveRequests: number;
  performanceScore: number;
}

interface SystemMetric {
  name: string;
  value: string;
  status: 'healthy' | 'warning' | 'critical';
  trend: 'up' | 'down' | 'stable';
}

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  title = 'HRIS Dashboard';
  
  dashboardStats: DashboardStat[] = [
    { label: 'Total Employees', value: '1,247', icon: 'üë•', change: '+12', trend: 'up' },
    { label: 'Active Projects', value: '23', icon: 'üìã', change: '+3', trend: 'up' },
    { label: 'Leave Requests', value: '8', icon: 'üìÖ', change: '-2', trend: 'down' },
    { label: 'Payroll Due', value: '$45,230', icon: 'üí∞', change: '+$2,450', trend: 'up' },
    { label: 'System Uptime', value: '99.9%', icon: 'üü¢', change: '+0.1%', trend: 'up' },
    { label: 'Active Users', value: '856', icon: 'üë§', change: '+45', trend: 'up' }
  ];

  departmentStats: DepartmentStat[] = [
    { name: 'IT', employeeCount: 245, activeProjects: 8, leaveRequests: 3, performanceScore: 92 },
    { name: 'HR', employeeCount: 45, activeProjects: 3, leaveRequests: 2, performanceScore: 88 },
    { name: 'Finance', employeeCount: 78, activeProjects: 4, leaveRequests: 1, performanceScore: 95 },
    { name: 'Operations', employeeCount: 156, activeProjects: 5, leaveRequests: 2, performanceScore: 90 }
  ];

  systemMetrics: SystemMetric[] = [
    { name: 'Server Response Time', value: '120ms', status: 'healthy', trend: 'stable' },
    { name: 'Database Performance', value: '98%', status: 'healthy', trend: 'up' },
    { name: 'API Success Rate', value: '99.8%', status: 'healthy', trend: 'up' },
    { name: 'Storage Usage', value: '75%', status: 'warning', trend: 'up' }
  ];

  recentActivities = [
    { action: 'New employee onboarded', user: 'John Smith', time: '2 hours ago', type: 'success' },
    { action: 'Leave request approved', user: 'Sarah Johnson', time: '4 hours ago', type: 'info' },
    { action: 'Payroll processed', user: 'System', time: '6 hours ago', type: 'success' },
    { action: 'Performance review due', user: 'Mike Wilson', time: '1 day ago', type: 'warning' }
  ];

  quickActions = [
    { name: 'Add Employee', icon: 'üë§', route: '/personnel-information-management' },
    { name: 'Process Payroll', icon: 'üí∞', route: '/payroll-management' },
    { name: 'Generate Report', icon: 'üìä', route: '/report-generation' },
    { name: 'View Attendance', icon: '‚è∞', route: '/timekeeping-attendance' }
  ];

  ngOnInit() {
    // TODO: Implement data fetching from services
  }
}
