import { Component, OnInit, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

interface DepartmentStat {
  name: string;
  count: number;
  percentage: number;
}

interface Employee {
  id: number;
  firstName: string;
  lastName: string;
  email: string;
  department: string;
  position: string;
  hireDate: string;
  status: string;
  profileImage?: string;
}

interface StatCard {
  id: string;
  title: string;
  value: number;
  change: number;
  icon: string;
  description: string;
  isVisible: boolean;
  color: string;
  dataType: 'number' | 'percentage' | 'rating';
}

@Component({
  selector: 'app-admin-dashboard',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule
  ],
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.scss']
})
export class AdminDashboardComponent implements OnInit {
  title = 'Personnel Information Management';

  // Dashboard stats
  totalEmployees = 345;
  employeeChange = 5;
  pendingRequests = 12;
  requestChange = -2;
  recentMovements = 23;
  movementChange = 3;

  // Analytics metrics
