import { Component, Inject, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { MatDialogRef, MAT_DIALOG_DATA, MatDialogModule } from '@angular/material/dialog';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';

interface Employee {
  id: number;
  name: string;
  email: string;
  department: string;
  position: string;
  hireDate: Date;
  salary: number;
  status: 'Active' | 'On Leave' | 'Terminated';
  profileImage?: string;
}

@Component({
  selector: 'app-edit-employee-modal',
  templateUrl: './edit-employee-modal.component.html',
  styleUrls: ['./edit-employee-modal.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatDialogModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    MatIconModule,
    MatDatepickerModule,
    MatNativeDateModule
  ]
})
export class EditEmployeeModalComponent implements OnInit {
  editForm: FormGroup;
  departments = [
    'IT',
    'HR',
    'Finance',
    'Marketing',
    'Operations',
    'Engineering',
    'Sales',
    'Customer Support',
    'Research',
    'Legal',
    'Product',
    'Design'
  ];

  statuses = ['Active', 'On Leave', 'Terminated'];

  constructor(
    private fb: FormBuilder,
    public dialogRef: MatDialogRef<EditEmployeeModalComponent>,
    @Inject(MAT_DIALOG_DATA) public data: { employee: Employee }
  ) {
    this.editForm = this.fb.group({
      name: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      department: ['', [Validators.required]],
      position: ['', [Validators.required]],
      hireDate: ['', [Validators.required]],
      salary: ['', [Validators.required, Validators.min(0)]],
      status: ['', [Validators.required]]
    });
  }

  ngOnInit(): void {
    if (this.data.employee) {
      this.editForm.patchValue({
        name: this.data.employee.name,
        email: this.data.employee.email,
        department: this.data.employee.department,
        position: this.data.employee.position,
        hireDate: this.data.employee.hireDate,
        salary: this.data.employee.salary,
        status: this.data.employee.status
      });
    }
  }

  onSubmit(): void {
    if (this.editForm.valid) {
      const updatedEmployee = {
        ...this.data.employee,
        ...this.editForm.value
      };
      this.dialogRef.close(updatedEmployee);
    }
  }

  onCancel(): void {
    this.dialogRef.close();
  }
}
